import React, { useContext } from 'react';
import FontContext from './FontContext';

function Accesibilidad() {
  const { isDyslexic, toggleFont } = useContext(FontContext);

  return (
    <main>
      <h1>Accesibilidad</h1>
      <p>
        Esta página te permite ajustar diferentes parámetros relacionados con la accesibilidad para fomentar el uso de nuestro servicio sin limitaciones.
      </p>
      <button onClick={toggleFont}>
        Cambiar a {isDyslexic ? 'Inter' : 'OpenDyslexic'}
      </button>
    </main>
  );
}

export default Accesibilidad;
