import React, { useContext } from 'react';
import FontContext from './FontContext';

function Accesibilidad() {
  const { isDyslexic, toggleFont } = useContext(FontContext);

  return (
    <div>
      <h1>Accesibilidad en la Tipografía</h1>
      <p>
        Esta página te permite cambiar la tipografía entre <strong>Inter</strong> y <strong>OpenDyslexic</strong>.
      </p>
      <button onClick={toggleFont}>
        Cambiar a {isDyslexic ? 'Inter' : 'OpenDyslexic'}
      </button>
    </div>
  );
}

export default Accesibilidad;
