import React, { createContext, useState, useEffect } from 'react';

// Crear el contexto
const FontContext = createContext();

export const FontProvider = ({ children }) => {
  // Estado inicial basado en localStorage
  const [isDyslexic, setIsDyslexic] = useState(() => {
    return localStorage.getItem('fontPreference') === 'dyslexic';
  });

  // Función para cambiar la fuente
  const toggleFont = () => {
    setIsDyslexic((prev) => {
      const newState = !prev;
      localStorage.setItem('fontPreference', newState ? 'dyslexic' : 'inter');
      return newState;
    });
  };

  // Aplicar la fuente en el body cuando cambia el estado
  useEffect(() => {
    if (isDyslexic) {
      document.body.classList.add('dyslexic');
    } else {
      document.body.classList.remove('dyslexic');
    }
  }, [isDyslexic]);

  return (
    <FontContext.Provider value={{ isDyslexic, toggleFont }}>
      {children}
    </FontContext.Provider>
  );
};

export default FontContext;
